function togglePixel(row,column,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM,white_or_black)
{
    var elem = document.getElementsByClassName(generatePixelClassName(row,column))[0];
    var prev_elem = getPreviousElem(row,column,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM);
    elem.classList.remove("rotate_white","rotate_black");
    
    elem.classList.add("pixel_refreshing");
    prev_elem.classList.remove("pixel_refreshing");
    //prev_elem.classList.remove("rotate_white","rotate_black");
    if(white_or_black == 0)
        {
            elem.classList.toggle("rotate_white");
        }
    else
        {
            elem.classList.toggle("rotate_black");
        }
}


function getPreviousElem(row,column,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM)
{
    if(column > 0)
        {
            return document.getElementsByClassName(generatePixelClassName(row,column-1))[0];
        }
    else if(column == 0 && row > 0)
        {
            return document.getElementsByClassName(generatePixelClassName(row-1,COLUMN_PIXEL_NUM-1))[0];
        }
    else if(column == 0 && row == 0)
        {
            return document.getElementsByClassName(generatePixelClassName(ROW_PIXEL_NUM-1,COLUMN_PIXEL_NUM-1))[0];
        }
}



