function displayAllInMyFont()
{
    if(j>=RELATIVE_POSI_H1 && j<RELATIVE_POSI_H0)
    {
        togglePixel(i,j,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(i,j-RELATIVE_POSI_H1,h1));
    }
    else if(j>=RELATIVE_POSI_H0 && j<RELATIVE_POSI_M1)
            {
                togglePixel(i,j,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(i,j-RELATIVE_POSI_H0,h0));
            }
    else if(j>=RELATIVE_POSI_M1 && j<RELATIVE_POSI_M0)
            {
                togglePixel(i,j,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(i,j-RELATIVE_POSI_M1,m1));
            }
    else if(j>=RELATIVE_POSI_M0 && j<RELATIVE_POSI_S1)
            {
                togglePixel(i,j,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(i,j-RELATIVE_POSI_M0,m0));
            }
    else if(j>=RELATIVE_POSI_S1 && j<RELATIVE_POSI_S0)
            {
                togglePixel(i,j,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(i,j-RELATIVE_POSI_S1,s1));
            }
    else if(j>=RELATIVE_POSI_S0 && j<COLUMN_PIXEL_NUM)
            {
                togglePixel(i,j,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(i,j-RELATIVE_POSI_S0,s0));
            }
    //window.requestAnimationFrame(displayAllInMyFont);
}

