function instantRefresh()
{
    for(var x=0;x<ROW_PIXEL_NUM;x++)
        {
            for(var y=0;y<COLUMN_PIXEL_NUM;y++)
                {
                        if(y>=RELATIVE_POSI_H1 && y<RELATIVE_POSI_H0)
                        {
                            togglePixel(x,y,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(x,y-RELATIVE_POSI_H1,h1));
                        }
                        else if(y>=RELATIVE_POSI_H0 && y<RELATIVE_POSI_M1)
                                {
                                    togglePixel(x,y,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(x,y-RELATIVE_POSI_H0,h0));
                                }
                        else if(y>=RELATIVE_POSI_M1 && y<RELATIVE_POSI_M0)
                                {
                                    togglePixel(x,y,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(x,y-RELATIVE_POSI_M1,m1));
                                }
                        else if(y>=RELATIVE_POSI_M0 && y<RELATIVE_POSI_S1)
                                {
                                    togglePixel(x,y,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(x,y-RELATIVE_POSI_M0,m0));
                                }
                        else if(y>=RELATIVE_POSI_S1 && y<RELATIVE_POSI_S0)
                                {
                                    togglePixel(x,y,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(x,y-RELATIVE_POSI_S1,s1));
                                }
                        else if(y>=RELATIVE_POSI_S0 && y<COLUMN_PIXEL_NUM)
                                {
                                    togglePixel(x,y,ROW_PIXEL_NUM,COLUMN_PIXEL_NUM ,!binarizeNumberDisplayMyFont(x,y-RELATIVE_POSI_S0,s0));
                                }
                }
        }
    document.getElementsByClassName(generatePixelClassName(ROW_PIXEL_NUM-1,COLUMN_PIXEL_NUM-1))[0].classList.remove("pixel_refreshing");
}




